import React, { useState } from "react";
import axios from "axios";

function App() {
  const [form, setForm] = useState({ name: "", email: "", phone: "" });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5050/register", form);
      setMessage(res.data.message);
    } catch (error) {
      setMessage(error.response?.data.message || "An error occurred.");
    }
  };

  const handleLoginGoogle = () => {
    window.location.href = "http://localhost:5050/auth/google";
  };

  return (
    <div className="app-container">
      {/* Header */}
      <header className="header">
        <img src="/logo.png" alt="NexGen Learning Logo" className="logo" />
        <h1 className="header-title">NexGen Learning</h1>
      </header>

      {/* Main Content */}
      <main className="main">
        <div className="form-container">
          <h2>Manual Registration</h2>
          <form onSubmit={handleRegister} className="form">
            <input
              name="name"
              placeholder="Name"
              value={form.name}
              onChange={handleChange}
              required
              className="input"
            />
            <input
              name="email"
              placeholder="Email"
              value={form.email}
              onChange={handleChange}
              required
              className="input"
            />
            <input
              name="phone"
              placeholder="Phone Number"
              value={form.phone}
              onChange={handleChange}
              required
              className="input"
            />
            <button type="submit" className="button primary">
              Register
            </button>
          </form>
          <button onClick={handleLoginGoogle} className="button secondary">
            Login with Google
          </button>
          {message && <p className="message">{message}</p>}
        </div>
      </main>

      {/* Footer */}
      <footer className="footer">
        <p>Â© 2024 NexGen Learning. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
